INSERT INTO `soal` (`id_soal`, `soal`, `kunci`, `kelas`, `id_mapel`, `id_jenis`, `listening`, `id_thj`) VALUES
(1, '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">&nbsp; Unsur konstitutif terbentuknya suatu negara terdiri dari &hellip;.</span></span></p>\r\n\r\n<ol style=\"list-style-type:lower-alpha\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Rakyat, Wilayah, Pemerintah</span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Rakyat, Wilayah, Pemerintah dan pengakuan&nbsp; negara lain</span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Pengakuan secara defacto dan dejure</span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Pengakuan secara nyata dan secara hokum</span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Rakyat, Wilayah, Pemerintahan yang berdaulat</span></span></li>\r\n</ol>\r\n', 'E', 'XII', 13, 2, '', 2),
(2, '<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Pancasila digunakan sebagai petunjuk arah semua kegiatan aktivitas hidup dan kehidupan di dalam segala bidang. Ini berarti bahwa semua tingkah laku dan tindak perbuatan setiap manusia Indonesia harus dijiwai dan merupakan pancaran dari semua sila di dalam pancasila. <span style=\"font-family:&quot;Times New Roman&quot;,serif\">Pernyataan tersebut merupakan kedudukan atau fungsi pancasila sebagai...</span></span></span></p>\r\n\r\n<ol style=\"list-style-type:lower-alpha\">\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">k</span><span style=\"font-family:&quot;Times New Roman&quot;,serif\">epribadian bangsa </span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">ideologi</span><span style=\"font-family:&quot;Times New Roman&quot;,serif\"> terbuka </span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">dasar negara</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">pandangan hidup</span><span style=\"font-family:&quot;Times New Roman&quot;,serif\"> bangsa Indonesia </span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">p</span><span style=\"font-family:&quot;Times New Roman&quot;,serif\">erjanjian luhur</span></span></span></li>\r\n</ol>\r\n', 'D', 'XII', 13, 2, '', 2),
(3, '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Contoh pengamalan&nbsp; nilai&nbsp; - nilai&nbsp; Pancasila sila kedua dalam lingkungan sekolah adalah &hellip;.</span></span></p>\r\n\r\n<ol style=\"list-style-type:lower-alpha\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">menjaga rasa persaudaraan</span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">saling bekerja sama anatar teman</span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">menghormati bapak / ibu dewan guru</span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">mengutamakan kepentingan&nbsp; anggota kelompok</span></span></li>\r\n	<li><span style=\"font-size:11.0pt\"><span style=\"font-family:&quot;Calibri&quot;,sans-serif\">dapat mengharagai pendapat teman</span></span></li>\r\n</ol>\r\n', 'C', 'XII', 13, 2, '', 2),
(4, '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Sepasang suami istri kewarganegaraan X menganut asas ius soli. Mereka tinggal di negara Y menganut asas ius sanguinis. Selama tinggal di negara Y, mereka melahirkan seorang anak. Bagaimanakah status warga negara si anak tersebut .....</span></span></p>\r\n\r\n<ol style=\"list-style-type:lower-alpha\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Bipatride </span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Apatride</span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Multipatridearga negara X</span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Warga negara Y</span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Warga negara X dan Y</span></span></li>\r\n</ol>\r\n', 'B', 'XII', 13, 2, '', 2),
(5, '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Berikut ini yang termasuk dalam suprastruktur politik adalah ....</span></span></p>\r\n\r\n<ol style=\"list-style-type:lower-alpha\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Presiden, MPR, DPR, DPD, MK, BPK</span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Presiden, MPR, DPR, Parpol, MK, BPK</span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Presiden, MPR, DPR, DPD, MK, LSM</span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">MPR,&nbsp; DPR, DPD, MK, BPK, Tokoh politik</span></span></li>\r\n	<li><span style=\"font-size:11.0pt\"><span style=\"font-family:&quot;Calibri&quot;,sans-serif\">Parpol, kelompok, LSM, tokoh politik</span></span></li>\r\n</ol>\r\n', 'A', 'XII', 13, 2, '', 2),
(6, '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Menurut Montesquieu kekuasaan negara dibagi menjadi tiga yaitu meliputi kekuasaan....</span></span></p>\r\n\r\n<ol style=\"list-style-type:lower-alpha\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">&nbsp;Legislatif, eksekutif dan federatif</span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Legislatif, eksekutif dan yudikatif</span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Eksekutif, federatif dan yudikatif</span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Eksaminatif, eksekutif dan moneter</span></span></li>\r\n	<li><span style=\"font-size:11.0pt\"><span style=\"font-family:&quot;Calibri&quot;,sans-serif\">Legislatif, konstitutif dan inspektif </span></span></li>\r\n</ol>\r\n', 'B', 'XII', 13, 2, '', 2),
(7, '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Tipe budaya politik tradisional, Islam, dan modern merupakan tipe budaya politik berdasar &hellip;..</span></span></p>\r\n\r\n<ol style=\"list-style-type:lower-alpha\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Struktur masyarakat Indonesia</span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Kedudukan masyarakat Indonesia</span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Karakteristik masyarakat Indonesia </span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Pak&nbsp; sosiologis</span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Tipe budaya politik yang berkembang di Indonesia</span></span></li>\r\n</ol>\r\n', 'E', 'XII', 13, 2, '', 2),
(8, '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\">Dalam pembentukan sebuah peraturan daerah tentunya harus dilandasi oleh hukum . Berikut yang bukan merupakan landasan hukum pembentukan pemerintah daerah adalah....</span></span></span></p>\r\n\r\n<ol style=\"list-style-type:lower-alpha\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\">UU No. 32 tahun 2004</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">UU No. 3 tahun 2002</span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\">UU No. 12 tahun 2008</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\">UU No. 23 tahun 2014</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:black\">Perpu No. 3 tahun 2005</span></span></span></li>\r\n</ol>\r\n', 'B', 'XII', 13, 2, '', 2),
(9, '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Factor pendorong integrasi adalah factor-faktor yang memudahkan bangsa Indonesia untuk bersatu antara lain&hellip;</span></span></p>\r\n\r\n<ol style=\"list-style-type:lower-alpha\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Penggunaan bahasa Indonesia sebagai bahasa nasional dan persatuan</span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">minimnya penghargaan terhadap kemajemukan yang bersifat heterogen</span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Kurangnya toleransi antargolongan</span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Kurangnya kesadaran dari masyarakat Indonesia terhadap ancaman dan ganguan dari luar</span></span></li>\r\n	<li><span style=\"font-size:11.0pt\"><span style=\"font-family:&quot;Calibri&quot;,sans-serif\">Adanya ketidakpuasan terhadap hasil pembangunan</span></span></li>\r\n</ol>\r\n', 'A', 'XII', 13, 2, '', 2),
