(23, '<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Di</span> <span style=\"font-size:14.0pt\">bawah ini yang bukan merupakan kharakteristik dari h</span><span style=\"font-size:14.0pt\">u</span><span style=\"font-size:14.0pt\">kum adalah &hellip;.</span></span></span></p>\r\n\r\n<ol style=\"list-style-type:lower-alpha\">\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Berbentuk peraturan</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Peraturan itu dapat tertulis maupun tidak tertulis.</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Bersifat memaksa atau berlakunya dapat dipaksakan.</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Paksaan harus dilakukan dengan bantuan alat-alat perlengkapan negara.</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Berupa himbauan untk semua warga Negara</span></span></span></li>\r\n</ol>\r\n', 'E', 'XII', 13, 2, '', 2),
(24, '<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Di</span> <span style=\"font-size:14.0pt\">bawah</span><span style=\"font-size:14.0pt\"> ini merupakan Contoh pengingkaran kewajiban sebagai warga negara adalah ...</span></span></span></p>\r\n\r\n<ol style=\"list-style-type:lower-alpha\">\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">menaati hukum yang berlaku</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">tidak membayar pajak</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">tidak memeluk agama</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">tidak mempunyai tempat tinggal tetap</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">penangkapan dan penahanan masyarakat sipil oleh aparat</span></span></span></li>\r\n</ol>\r\n', 'B', 'XII', 13, 2, '', 2),
