(30, '<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Dibawah</span><span style=\"font-size:14.0pt\"> ini yang bukan termasuk factor penyebab adanya tindakan korupsi adalah &hellip;&hellip;</span></span></span></p>\r\n\r\n<ol style=\"list-style-type:lower-alpha\">\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">kurangnya kesadaran hokum </span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">pendapatan tidak mencukupi</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">adanya kesempatan untuk tejadinya korupsi</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">lemahnya control social </span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">kurangnya kerjasama dengan rekan kerja</span></span></span></li>\r\n</ol>\r\n', 'E', 'XII', 13, 2, '', 2),
(31, '<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Dibawah</span><strong> </strong><span style=\"font-size:14.0pt\">ini adalah dampak positif kemajuan iptek di bidang ekonomi adalah &hellip;..</span></span></span></p>\r\n\r\n<ol style=\"list-style-type:lower-alpha\">\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">produktivitas dunia usaha semakin meningkat </span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">meningkatnya nilai-nilai demokrasi </span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">mempermudah komunikasi antar manusia </span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">semakin berkembangnya daya pik</span><span style=\"font-size:14.0pt\">i</span><span style=\"font-size:14.0pt\">r individu dalam suatu bidang </span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">kita dapat lebih cepat mendapatkan informasi-informasi yang akurat</span></span></span></li>\r\n</ol>\r\n', 'A', 'XII', 13, 2, '', 2),
(32, '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Berikut ini yang merupakan dampak negatif kemajuan pengetahuan dan tehnologi bagi negara dibidang sosial adalah ....</span></span></span></p>\r\n\r\n<ol style=\"list-style-type:lower-alpha\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Timbulnya sifat konsuntif</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Kerahasian alat tes semakin terancam</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Pemenuhan kebutuhan akan</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Kemerosotan moral dikalangan masyarakat khususnya remaja</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Dapat berkomunikasi dengan teman ataupun kerabat yang sangat jauh</span></span></span></li>\r\n</ol>\r\n', 'D', 'XII', 13, 2, '', 2),
(33, '<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Berikut</span><span style=\"font-size:14.0pt\"><span style=\"color:black\"> yang merupakan dampak negative&nbsp; kemajuan iptek bagi Negara di bidang social adalah &hellip;..</span></span></span></span></p>\r\n\r\n<ol style=\"list-style-type:lower-alpha\">\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"color:black\">d</span></span><span style=\"font-size:14.0pt\"><span style=\"color:black\">apat berkomunikasi dengan teman atau kerabat yang sangat jauh </span></span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"color:black\">p</span></span><span style=\"font-size:14.0pt\"><span style=\"color:black\">emenuhan kebutuhan akan fasilitas pendidikan dapat dipenuhi </span></span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"color:black\">k</span></span><span style=\"font-size:14.0pt\"><span style=\"color:black\">emerosotan moral dikalangan masyarakat khususnya remaja </span></span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"color:black\">t</span></span><span style=\"font-size:14.0pt\"><span style=\"color:black\">imbulnya sifat konsumtif</span></span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"color:black\">k</span></span><span style=\"font-size:14.0pt\"><span style=\"color:blac