(34, '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Agar kemajuan IPTEK tidak merusak nilai &ndash; nilai keagamaan, maka sebaiknya ....</span></span></span></p>\r\n\r\n<ol style=\"list-style-type:lower-alpha\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">meningkatkan kadar keimanan dan ketaqwaan</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">meningkatkan tehnologi informasi melalui media sosial</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">meningkatkan tehnologi informasi melalui media tv</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Penerapan dan penggunaan IPTEK sesuai dengan nilai &ndash; nilai agama</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">dikembangkan sekolah &ndash; sekolah agama</span></span></span></li>\r\n</ol>\r\n', 'D', 'XII', 13, 2, '', 2),
(35, '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Sekelompok manusia yang berada dalam suatu wilayah tertentu yang mempunyai karakter, identitas atau budaya yang khas disebut .....</span></span></span></p>\r\n\r\n<ol style=\"list-style-type:lower-alpha\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">negara</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">bangsa</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">negara dan bangsa</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">kedaulatan wilayah</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">pemerintahan yang berdaulat</span></span></span></li>\r\n</ol>\r\n', 'A', 'XII', 13, 2, '', 2),
(36, '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Rumusan Tujuan Negara Republik Indonesia terdapat dalm pembukaan UUD 1945 pada alinea .....</span></span></span></p>\r\n\r\n<ol style=\"list-style-type:lower-alpha\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">pertama</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">kedua</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">ketiga</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">keempat</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">disemua alinea</span></span></span></li>\r\n</ol>\r\n', 'D', 'XII', 13, 2, '', 2),
(37, '<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"color:black\">Salah</span></span><span style=\"font-size:14.0pt\"> satu prinsip persatuan dan kesatuan yang mengadung makna sebuah kebanggan terhadap tanah air tanpa merendahkan bangsa lain adalah &hellip;&hellip;</span></span></span></p>\r\n\r\n<ol style=\"list-style-type:lower-alpha\">\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">bhineka tunggal ika</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">nasionalisme indonesia</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">kebebasan yang bertanggung jawab</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">wawasan nusantara</span></span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">persatuan pembanguna untuk mewujudkan cita-cita reformasi</span></span></span></li>\r\n</ol>\r\n', 'B', 'XII', 13, 2, '', 2),
(38, '<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"color:black\">P</span></span><span style=\"font-size:14.0pt\"><span style=\"color:black\">erhatikan</span></span><span style=\"font-size:14.0pt\"> pernyataan berikut ini!</span></span></span></p>\r\n\r\n<ol>\r\n	<li style=\"list-style-type:none\">\r\n	<ol>\r\n		<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Kebangkitan nasional </span></span></span></li>\r\n		<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Sumpah pemuda </span></span></span></li>\r\n		<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Perasaan senasib </span></span></span></li>\r\n		<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Proklamasi kemerdekaan </span></span></span></li>\r\n	</ol>\r\n	</li>\r\n</ol>\r\n\r\n<p style=\"margin-left:0cm; margin-right:0cm; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Urutan tahap-tahap pembinaan persatuan bangsa Indonesia adalah &hellip;.</span></span></span></p>\r\n\r\n<ol>\r\n	<li style=\"list-style-type:none\">\r\n	<ol style=\"list-style-type:lower-alpha\">\r\n		<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">1, 2, 3, dan 4</span></span></span></li>\r\n		<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">3, 1, 2 dan 4</span></span></span></li>\r\n		<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">3, 1, 4 dan 2</span></span></span></li>\r\n		<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">1, 2, 4 dan 3</span></span></span></li>\r\n		<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">1, 3, 2 dan 4</span></span></span></li>\r\n	</ol>\r\n	</li>\r\n</ol>\r\n', 'B', 'XII', 13, 2, '', 2),
(39, '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Semangat persatuan dan kesatuan dari bangsa indonesia akhirnya terwujud dengan terbentuknya ...</span></span></span></p>\r\n\r\n<ol style=\"list-style-type:lower-alpha\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">satu tekad yaitu mewujudkan masyarakat adil dan makmur</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">satu kebudayaan yaitu kebudayaan nasional Indonesia </span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">kesepakataan tentang dasar negara dan konstitusi negara</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">satu semboyan yaitu bersatu kita teguh bercerai kita runtuh</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">satu negara yaitu negara kesatuan republik indonesia</span></span></span></li>\r\n</ol>\r\n', 'E', 'XII', 13, 2, '', 2),
(40, '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Berikut ini merupakan pengertian pers menurut UU No. 40 Tahun 1999 tentang pers yaitu&hellip;</span></span></span></p>\r\n\r\n<ol style=\"list-style-type:lower-alpha\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Alat untuk menjepit atau memadatkan</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Alat cetak untuk mencetak atau surat kabar</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Penyiaran-penyiaran pikiran, gagasan, ayau berita-berita dengan kata tertulis</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Lembaga sosial dan wahana komunikasi massa yang melaksanakan kegiatan jurnalistik</span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Orang yang bekerja di bidang persurat kabaran</span></span></span></li>\r\n</ol>\r\n', 'D', 'XII', 13, 2, '', 2),
