(241, '<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Seorang anak berteriak di antara 2 tebing. Bunyi pantul dari tebing B terdengar 1 detik setelah bunyi asli, dan 1 detik kemudian terdengar bunyi pantul dari tebing A. Jika&nbsp; tebing A berada pada jarak 340 m dari anak tersebut, maka jarak kedua tebing tersebut adalah ....</span></span></span></span></p>\r\n\r\n<ol style=\"list-style-type:upper-alpha\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">510 m</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">425 m</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">340 m</span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">170 m</span></span></span></span></li>\r\n</ol>\r\n', 'A', 'IX', 12, 1, '', 2),
(242, '<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Benda terletak di depan cermin cekung sejauh 15 cm dari cermin. Terbentuk bayangan maya pada jarak 30 cm dari cermin. Jika benda digeser 20 cm menjauhi cermin dari posisi semula, terbentuk bayangan baru bersifat &hellip;.</span></span></span></span></p>\r\n\r\n<ol style=\"list-style-type:upper-alpha\">\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">maya, tegak, diperbesar </span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">nyata, terbalik, diperbesar </span></span></span></span></li>\r\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">nyata, terbalik diperkecil </span></span></span></span></li>\r\n	<li><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">nyata, terbalik, sama besar</span></span></li>\r\n</ol>\r\n', 'B', 'IX', 12, 1, '', 2),
